import React, { useState, useEffect, useContext } from 'react';
import { conectSpotifyWithCode } from '../services/spotifyService'
import { connectionsContext } from '../context'
import { withRouter } from 'react-router-dom';
import Login from './Login';

const Home = (props)=>{
  const urlSearch = new URLSearchParams(props.location.search)
  const [connection, setConnection] = useContext(connectionsContext)

  async function getTokenAuthorization(props){

    if(localStorage.access_token == "" || localStorage.refresh_token == ""){
      const tokens = await conectSpotifyWithCode(
        'http://localhost:3000/home', 
        urlSearch.get('code'), 
        'https://accounts.spotify.com/api/token', 
        '5b9475166c654131bb402fb4a6e463aa:875b53042f3b4787b502d4f81b5559c4'
      )
      localStorage.access_token = tokens.access_token
      localStorage.refresh_token = tokens.refresh_token
      setConnection({access_token: localStorage.access_token, refresh_token: localStorage.refresh_token})
    }
    console.log(localStorage.access_token, localStorage.refresh_token)
  }

  function cleanConnection(){
    console.log(localStorage.access_token, localStorage.refresh_token)
    localStorage.access_token = ""
    localStorage.refresh_token = ""
    // props.history.goBack(null)
  }

  useEffect(() => { 
    getTokenAuthorization()  
  }, [])

  return (
    <div>
      <p>seu access token: {connection.access_token??connection.error}</p>
      <p>seu refresh token: {connection.refresh_token??connection.error_description}</p>
      <button onClick={cleanConnection}>zerar bangue</button>
    </div>
  );
}

export default Home;
