import React, { useState, useEffect } from 'react';
import { conectSpotifyWithCode } from '../services/spotifyService'

const Home = (props)=>{
  const urlSearch = new URLSearchParams(props.location.search)
  const [configSpotify, setConfigSpotify] = useState({access_token: '', refresh_token: ''})

  async function ueh(){
    if(configSpotify.access_token === '' || configSpotify.refresh_token === ''){
      //setConfigSpotify(
        await conectSpotifyWithCode(
          'http://localhost:3000/home', 
          urlSearch.get('code'), 
          'https://accounts.spotify.com/api/token', 
          '5b9475166c654131bb402fb4a6e463aa:875b53042f3b4787b502d4f81b5559c4'
        )
      //)
      console.log('2')
      console.log(configSpotify)
    }
  }

  useEffect(() => { 
    ueh()
  }, [setConfigSpotify, configSpotify])

  return (
    <div>
      <p>acces token {configSpotify.access_token}</p>
      <p>refresh token {configSpotify.refresh_token}</p>
    </div>
  );
}

export default Home;
