import querystring from 'querystring'
import request from 'request'

export async function conectSpotifyWithToken(callbackUrl, code, urlSpotify, base64ClientIdSecrect){
    const authOptions = { 
        url: urlSpotify,
        form: {
            grant_type: 'authorization_code',
            code: code,
            redirect_uri:callbackUrl,
        },
        headers:{
            'Authorization': 'Basic '+Buffer(base64ClientIdSecrect).toString('base64')
        },
        json: true
    }

    await request.post(authOptions, async (error, response, body)=>{
        console.log('1')
        if(!error && response.statusCode === 200){
            return body
        }else{
            return { error: error, body: body,response: response }
        }
    })
}

export function loginSpotify(clientId, callbackUrl, urlSpotify){
    window.location.href = urlSpotify/**/+
    querystring.stringify({
        response_type: 'code',
        client_id:clientId,
        scope: 'user-read-private user-read-email', 
        redirect_uri: callbackUrl
    })
}
