import React, { useState, useEffect } from 'react';

const App = ()=>{
  const [count, setCount] = useState(0);

  function click(){
    setCount(count + 1)
  }

  useEffect(() => {
    document.title = `Você clicou ${count} vezes`;
  });

  return (
    <div>
      <p>Você clicou {count} vezes</p>
      <button onClick={click()}>
        Clique aqui
      </button>
    </div>
  );
}

export default App;
