import querystring from 'querystring'
import request from 'request'

export function conectSpotifyWithToken(callbackUrl, code, urlSpotify, base64ClientIdSecrect){
    const authOptions = { 
        url: urlSpotify,
        form: {
            grant_type: 'authorization_code',
            code: code,
            redirect_uri:callbackUrl,
        },
        headers:{
            'Authorization': 'Basic '+Buffer(base64ClientIdSecrect).toString('base64')
        },
        json: true
    }
    request.post(authOptions, (error, response, body)=>{
        if(!error && response.statusCode === 200){
            return body
        }else{
            return {
                error: error,
                body: body,
                response: response
            }
        }
    })
}

export function logarSpotify(clientId, callbackUrl, urlSpotify){
    window.location.href = urlSpotify/*'https://accounts.spotify.com/authorize?'*/+
    querystring.stringify({
        response_type: 'code',
        client_id:clientId/*'5b9475166c654131bb402fb4a6e463aa'*/,
        scope: 'user-read-private user-read-email', 
        redirect_uri: callbackUrl/*'http://localhost:3000/home'*/
    })
}
