const mpActionsType = {
    addConnection: (state, action) => {
        console.log('entrou aqui', action)
        return {access_token: action.acess_token, ref}
    },
    removeConnection: (state, action) => {
        return {acess_token: "nada", refresh_token:"nada"}
    }
}

export const spotifyConnection = (state = {acess_token: "nada", refresh_token:"nada"}, action) =>{
    if(!mpActionsType[action.type]){
        return state
    }
    return mpActionsType[action.type](state, action)
}
