import React, { useState, useEffect } from 'react';
import { conectSpotifyWithCode } from '../services/spotifyService'
import {useSelector, useDispatch} from 'react-redux'
import { addConnectionAction } from '.././actions/spotifyActions'

const Home = (props)=>{
  const urlSearch = new URLSearchParams(props.location.search)
  const connections = useSelector(state => state.spotifyConnection)
  const dispatch = useDispatch()
  const tokens = {}

  async function getTokenAuthorization(){
    tokens = await conectSpotifyWithCode(
      'http://localhost:3000/home', 
      urlSearch.get('code'), 
      'https://accounts.spotify.com/api/token', 
      '5b9475166c654131bb402fb4a6e463aa:875b53042f3b4787b502d4f81b5559c4'
    )
  }

  useEffect(() => { 
    if(tokens == {}){

      // dispatch(addConnectionAction(tokens))
    }
    console.log('antes', connections)
    getTokenAuthorization()
    console.log('depois', connections)
  }, [urlSearch.get('code'), tokens])

  return (
    <div>
      <p>connections</p>
    </div>
  );
}

export default Home;
