import { Spotify } from 'spotify-web-api-js'

const Home = (props)=>{
  const urlSearch = new URLSearchParams(props.location.search)
  const [configSpotify, setConfigSpotify] = useState({access_token: '', refresh_token: ''})

  async function ueh(){
    if(configSpotify.access_token === '' || configSpotify.refresh_token === ''){
      //setConfigSpotify(

        s = new Spotify({
          clientId: '5b9475166c654131bb402fb4a6e463aa',
          clientSecret: '875b53042f3b4787b502d4f81b5559c4',
          redirectUri: 'http://localhost:3000/home'
        })

        s.getMe()
        .then(function(data) {
          console.log('Some information about the authenticated user', data.body);
        }, function(err) {
          console.log('Something went wrong!', err);
        });

        // await conectSpotifyWithCode(
