import querystring from 'querystring'

export async function conectSpotifyWithCode(callbackUrl, code, urlSpotify, base64ClientIdSecrect){
    let formData = new FormData();
    formData.append('code', code);
    formData.append('grant_type', 'authorization_code');
    formData.append('redirect_uri', code);
    console.log(code, )
    return await fetch(urlSpotify, {
        method: 'POST',
        headers: {
            'Content-Type': "application/x-www-form-urlencoded; charset=UTF-8",
            'Authorization': 'Basic '+Buffer(base64ClientIdSecrect).toString('base64')
        },
        body: formData
    })
    .then(response => response.json())
    .then(json => {
        console.log(1)
        return json
    })
}

export function loginSpotify(clientId, callbackUrl, urlSpotify){
    window.location.href = urlSpotify/**/+
    querystring.stringify({
        response_type: 'code',
        client_id:clientId,
        scope: 'user-read-private user-read-email', 
        redirect_uri: callbackUrl
    })
}
