import React, { useState, useEffect } from 'react';
import queryString from 'querystring'
import request from 'request'

const Home = (props)=>{
  const urlSearch = new URLSearchParams(props.location.search)
  const [configSpotify, setConfigSpotify] = useState({access_token: '', refresh_token: ''})

  useEffect(() => { 
      let authOptions = {
        url: 'https://accounts.spotify.com/api/token',
        form: {
            grant_type:    'authorization_code',
            code:          urlSearch.get('code'),
            redirect_uri:  'http://localhost:3000/home',
        },
        headers:{
            'Authorization': 'Basic '+Buffer('5b9475166c654131bb402fb4a6e463aa:875b53042f3b4787b502d4f81b5559c4').toString('base64')
        },
        json: true
      }

      request.post(authOptions, (error, response, body)=>{
        if(error)
        setConfigSpotify(body)
      })
  });

  return (
    <div>
      <p>acces token {configSpotify.access_token}</p>
      <p>refresh token {configSpotify.refresh_token}</p>
    </div>
  );
}

export default Home;
