import React, { useState, useEffect } from 'react';
import { conectSpotifyWithCode } from '../services/spotifyService'
import { connectionsContext } from '../context'

const Home = (props)=>{
  const urlSearch = new URLSearchParams(props.location.search)

  async function getTokenAuthorization(){
    const tokens = await conectSpotifyWithCode(
      'http://localhost:3000/home', 
      urlSearch.get('code'), 
      'https://accounts.spotify.com/api/token', 
      '5b9475166c654131bb402fb4a6e463aa:875b53042f3b4787b502d4f81b5559c4'
    )
  }

  useEffect(() => { 
    getTokenAuthorization()  
  }, [getTokenAuthorization])

  return (
    <div>
      <connectionsContext.Consumer>
        {}
      </connectionsContext.Consumer>
    </div>
  );
}

export default Home;
