import React, { useState, useEffect, useContext } from 'react';
import { conectSpotifyWithCode } from '../services/spotifyService'
import { connectionsContext } from '../context'
import { withRouter } from 'react-router-dom';
import Login from './Login';

const Home = (props)=>{
  const urlSearch = new URLSearchParams(props.location.search)
  const [connection, setConnection] = useContext(connectionsContext)

  async function getTokenAuthorization(){
    const tokens = await conectSpotifyWithCode(
      'http://localhost:3000/home', 
      urlSearch.get('code'), 
      'https://accounts.spotify.com/api/token', 
      '5b9475166c654131bb402fb4a6e463aa:875b53042f3b4787b502d4f81b5559c4'
    )
    setConnection(tokens)
    localStorage 
    console.log(tokens)
  }

  useEffect(() => { 
    getTokenAuthorization()  
  }, [])

  return (
    <div>
      <p>seu access token: {connection.access_token??connection.error}</p>
      <p>seu refresh token: {connection.refresh_token??connection.error_description}</p>
    </div>
  );
}

export default Home;
